CREATE OR REPLACE FUNCTION eliseo.correo_persona ( v_id_persona integer) RETURNS varchar AS $body$
DECLARE

	p_correo	  varchar(200);


BEGIN

    select correo  into STRICT p_correo
    from moises.persona_natural
    where id_persona=v_id_persona;

return p_correo;

END;


