CREATE OR REPLACE FUNCTION eliseo.fc_celular (P_ID_PERSONA bigint) RETURNS varchar AS $body$
DECLARE

    L_CELULAR varchar(50);

BEGIN 
       
		select 
		--case when CELULAR = 'N' then '' else CELULAR end 
		MAX(CELULAR)
		INTO STRICT L_CELULAR
		from moises.persona_natural where ID_PERSONA=P_ID_PERSONA;
	   --and rownum=1;
		IF coalesce(L_CELULAR::text, '') = '' THEN
			SELECT
			        coalesce(max(NUM_TELEFONO),'') INTO STRICT L_CELULAR
			FROM MOISES.PERSONA_TELEFONO pt 
			WHERE ID_PERSONA = P_ID_PERSONA
			AND ES_ACTIVO=1  LIMIT 1;
		END IF;
    RETURN(L_CELULAR);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
