CREATE OR REPLACE FUNCTION eliseo.fc_codigo_alumno (P_ID_CLIENTE bigint) RETURNS varchar AS $body$
DECLARE

  l_datos varchar(250);
 datos CURSOR FOR
                SELECT
                CODIGO AS NOMBRE
                FROM VW_PERSONA_NATURAL_ALUMNO
                WHERE ID_PERSONA=P_ID_CLIENTE;

BEGIN
       open datos;
       fetch datos into l_datos;
       if not datos%found then
         l_datos := ' ';
       end if;
       close datos;
       RETURN( l_datos );

        
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
