CREATE OR REPLACE FUNCTION eliseo.fc_colp_obt_depto_almacen (P_ID_ALMACEN bigint) RETURNS bigint AS $body$
DECLARE

CONTAR integer:=0;
ID_DEPTO bigint:= NULL;


BEGIN
   SELECT COUNT(*) INTO STRICT CONTAR
		FROM INVENTARIO_ALMACEN IA
		INNER JOIN INVENTARIO_ALMACEN_RUBRO IR ON IA.ID_ALMACEN = IR.ID_ALMACEN
		WHERE IA.ESTADO='1' AND IR.ID_RUBRO=1 AND IA.ID_ALMACEN=P_ID_ALMACEN;

   IF CONTAR > 0 THEN
		SELECT IA.ID_DEPTO INTO STRICT ID_DEPTO
		FROM INVENTARIO_ALMACEN IA
		INNER JOIN INVENTARIO_ALMACEN_RUBRO IR ON IA.ID_ALMACEN = IR.ID_ALMACEN
		WHERE IA.ESTADO='1' AND IR.ID_RUBRO=1 AND IA.ID_ALMACEN=P_ID_ALMACEN;
   END IF;
	RETURN(ID_DEPTO);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
