CREATE OR REPLACE FUNCTION eliseo.fc_depto_cliente (P_ID_CLIENTE bigint) RETURNS varchar AS $body$
DECLARE

  l_depto varchar(10);
  l_contar bigint;

BEGIN

  select count(*) into STRICT l_contar
  FROM APS_PLANILLA A
  WHERE A.ID_PERSONA = P_ID_CLIENTE  AND
  A.ID_ANHO||lpad(A.id_mes,2,'00') IN (  
     SELECT  max(B.ID_ANHO||lpad(B.id_mes,2,'00'))
     FROM APS_PLANILLA B
     WHERE B.ID_PERSONA = P_ID_CLIENTE
  );

  if l_contar> 1 then
    SELECT x.ID_DEPTO into STRICT  l_depto
    FROM (
      SELECT A.ID_DEPTO
          FROM APS_PLANILLA A
          WHERE A.ID_PERSONA = P_ID_CLIENTE  AND
          A.ID_ANHO||lpad(A.id_mes,2,'00') IN (  
             SELECT  max(B.ID_ANHO||lpad(B.id_mes,2,'00'))
             FROM APS_PLANILLA B
             WHERE B.ID_PERSONA =P_ID_CLIENTE
          ) 
        order by A.id_contrato desc
    ) x LIMIT 1;
  else
    if l_contar=1 then

      select A.ID_DEPTO into STRICT  l_depto
      FROM APS_PLANILLA A
      WHERE A.ID_PERSONA = 18940  AND
      A.ID_ANHO||lpad(A.id_mes,2,'00') IN (  
         SELECT  max(B.ID_ANHO||lpad(B.id_mes,2,'00'))
         FROM APS_PLANILLA B
         WHERE B.ID_PERSONA = P_ID_CLIENTE
      );
    end if;
  end if;

  return l_depto;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
