CREATE OR REPLACE FUNCTION eliseo.fc_dias_laborables (fecha_inicio timestamp(0), fecha_fin timestamp(0)) RETURNS bigint AS $body$
DECLARE

      numero_dias bigint := 0;
      fecha_actual timestamp(0);

BEGIN
      IF fecha_fin >= fecha_inicio THEN    
          fecha_actual := fecha_inicio;
          WHILE fecha_actual <= fecha_fin LOOP
             IF TO_CHAR(fecha_actual,'DY') NOT IN ('SÁB','DOM') THEN
             	numero_dias := numero_dias + 1;
             END IF;
             fecha_actual := fecha_actual + 1;
         END LOOP;
         RETURN numero_dias;
     ELSE
     	RETURN 0;
     END IF;
 END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
