CREATE OR REPLACE FUNCTION eliseo.fc_dinamica_asiento_ctacte (P_ID_ASIENTO integer) RETURNS varchar AS $body$
DECLARE

s_ctacte varchar(20);
s_resul varchar(1000);
p_conta integer;

ASIENTO_CTACTE CURSOR FOR
SELECT
ID_CTACTE 
FROM CONTA_DINAMICA_CTA_CTE
WHERE ID_ASIENTO = P_ID_ASIENTO
ORDER BY ID_CTACTE;



BEGIN
    p_conta:=1;
    open ASIENTO_CTACTE;
        fetch ASIENTO_CTACTE into
            s_ctacte;
        while ASIENTO_CTACTE%found loop
            if p_conta = 1 then
                s_resul := s_ctacte;
            else
                s_resul := s_resul||', '||s_ctacte;
            end if;

            fetch ASIENTO_CTACTE into
                s_ctacte;
            p_conta:=p_conta+1;
        end loop;
	close ASIENTO_CTACTE;	
	
	RETURN(s_resul);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
