CREATE OR REPLACE FUNCTION eliseo.fc_email (P_ID_PERSONA bigint) RETURNS varchar AS $body$
DECLARE

    L_EMAIL varchar(80);

BEGIN 
       
   		select case when correo = 'N' then '' else correo end INTO STRICT L_EMAIL
		from moises.persona_natural where ID_PERSONA=P_ID_PERSONA  LIMIT 1;
		IF coalesce(L_EMAIL::text, '') = '' THEN
			SELECT
			        coalesce(max(DIRECCION),'') INTO STRICT L_EMAIL
			FROM MOISES.PERSONA_VIRTUAL
			WHERE ID_PERSONA = P_ID_PERSONA
			AND ES_ACTIVO=1  LIMIT 1;
		END IF;
    RETURN(L_EMAIL);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
