CREATE OR REPLACE FUNCTION eliseo.fc_id_persona (P_DOCUMENTO text) RETURNS bigint AS $body$
DECLARE

  l_datos bigint;
 datos CURSOR FOR
                SELECT
                ID_PERSONA
                FROM VW_PERSONA_NATURAL
                WHERE NUM_DOCUMENTO = P_DOCUMENTO;


BEGIN
       open datos;
       fetch datos into l_datos;
       if not datos%found then
         l_datos := 0;
       end if;
       close datos;
       RETURN( l_datos );

        
END;
$body$
