CREATE OR REPLACE FUNCTION eliseo.fc_id_proveedor (P_ID_RUC bigint) RETURNS bigint AS $body$
DECLARE

    L_ID_PERSONA bigint;

BEGIN
        SELECT ID_RUC INTO STRICT L_ID_PERSONA FROM (
        SELECT ID_PERSONA AS ID_RUC
        FROM MOISES.VW_PERSONA_JURIDICA
        WHERE ID_RUC = P_ID_RUC

UNION ALL

        SELECT ID_PERSONA AS ID_RUC
        FROM MOISES.VW_PERSONA_NATURAL
        WHERE NUM_DOCUMENTO = P_ID_RUC
        AND ID_TIPODOCUMENTO=6
         ) X LIMIT 1;

        RETURN(L_ID_PERSONA);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
