CREATE OR REPLACE FUNCTION eliseo.fc_igv (P_FECHA timestamp(0)) RETURNS bigint AS $body$
DECLARE

l_igv decimal(10,2);
 datos CURSOR FOR
 SELECT id_igv
        FROM conta_igv
        WHERE (p_fecha between fecha_ini and coalesce(fecha_fin,current_date));


BEGIN
       open datos;
       fetch datos into l_igv;
       if not datos%found then
         l_igv := 0;
       end if;
       close datos;
       RETURN( l_igv );

        
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
