CREATE OR REPLACE FUNCTION eliseo.fc_importe_evento (P_ID_EVENTO_PARTICIPANTE integer) RETURNS bigint AS $body$
DECLARE

L_IMPORTE bigint:=0;

BEGIN

 SELECT sum(eetb.importe) INTO STRICT L_IMPORTE
FROM jonas.EVENTO_EVENTO_PARTICIPANTE eep
INNER JOIN jonas.EVENTO_PAQUETE_TALLER ept ON eep.ID_PAQUETE = ept.ID_PAQUETE
INNER JOIN jonas.EVENTO_PARTICIPANTE_TALLER ept2 ON ept.ID_EVENTO_TALLER = ept2.ID_EVENTO_TALLER AND eep.ID_EVENTO_PARTICIPANTE = ept2.ID_EVENTO_PARTICIPANTE
join jonas.evento_evento_taller eetb on eetb.id_evento_taller = ept.id_evento_taller
WHERE eep.id_evento_participante = P_ID_EVENTO_PARTICIPANTE
group by eep.ID_EVENTO_PARTICIPANTE;

	RETURN(L_IMPORTE);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
