CREATE OR REPLACE FUNCTION eliseo.fc_lote_voucher (P_ID_VOUCHER integer) RETURNS varchar AS $body$
DECLARE

L_NOMBRE varchar(100);

BEGIN
        SELECT
                coalesce(LOTE, '*'||ID_TIPOASIENTO||' '||NUMERO )||' '||TO_CHAR(FECHA,'DD/MM') into STRICT L_NOMBRE
        FROM ELISEO.CONTA_VOUCHER
        WHERE ID_VOUCHER = P_ID_VOUCHER;

        RETURN(L_NOMBRE);

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
