CREATE OR REPLACE FUNCTION eliseo.fc_mes_name (p_mes text) RETURNS varchar AS $body$
DECLARE

   l_mes_nombre varchar(30);
   l_mes_numero varchar(30);

BEGIN
	 l_mes_numero:=p_mes;
	 l_mes_nombre:='---';
	 if l_mes_numero='01' then
	 	l_mes_nombre := 'Enero.';
	 end if;

	 if l_mes_numero='02' then
	 	l_mes_nombre := 'Febrero.';
	 end if;

	 if l_mes_numero='03' then
	 	l_mes_nombre := 'Marzo';
	 end if;

	 if l_mes_numero='04' then
	 	l_mes_nombre := 'Abril';
	 end if;

	 if l_mes_numero='05' then
	 	l_mes_nombre := 'Mayo';
	 end if;

	 if l_mes_numero='06' then
	 	l_mes_nombre := 'Junio';
	 end if;

	 if l_mes_numero='07' then
	 	l_mes_nombre := 'Julio';
	 end if;

	 if l_mes_numero='08' then
	 	l_mes_nombre := 'Agosto';
	 end if;

	 if l_mes_numero='09' then
	 	l_mes_nombre := 'Setiembre';
	 end if;

	 if l_mes_numero='10' then
	 	l_mes_nombre := 'Octubre';
	 end if;
	
 	 if l_mes_numero='11' then
	 	l_mes_nombre := 'Noviembre';
	 end if;

	 if l_mes_numero='12' then
	 	l_mes_nombre := 'Diciembre';
	 end if;
	
	 
	 RETURN( l_mes_nombre );
END;
