CREATE OR REPLACE FUNCTION eliseo.fc_numero_venta (P_ID_VENTA bigint) RETURNS varchar AS $body$
DECLARE

    L_NUMERO varchar(8);

BEGIN 
       
    SELECT
            coalesce(NUMERO,NUMERO_REF) INTO STRICT L_NUMERO
    FROM VENTA
    WHERE ID_VENTA = P_ID_VENTA;
    RETURN(L_NUMERO);
END;
$body$
LANGUAGE PLPGSQL
