CREATE OR REPLACE FUNCTION eliseo.fc_obtener_cargo (P_ID_CONTRATO bigint, P_ID_PERSONA bigint, P_ID_ENTIDAD bigint) RETURNS varchar AS $body$
DECLARE

l_dato varchar(100):='';
l_contar integer:=0;
l_id_virtual bigint;
anho bigint;
mes bigint;


BEGIN
   select COUNT(*) INTO STRICT l_contar from APS_PLANILLA WHERE ID_CONTRATO=P_ID_CONTRATO;

   IF l_contar>0 THEN
     IF l_contar=1 THEN
      SELECT NOM_CARGO INTO STRICT l_dato FROM APS_PLANILLA WHERE ID_CONTRATO=P_ID_CONTRATO AND ID_PERSONA = P_ID_PERSONA AND ID_ENTIDAD = P_ID_ENTIDAD;
     ELSE
		
			SELECT SUBSTR((SELECT "MAX"("CONCAT"(ID_ANHO, ID_MES)) FROM APS_PLANILLA WHERE APS_PLANILLA.ID_CONTRATO = P_ID_CONTRATO
				AND APS_PLANILLA.ID_PERSONA = P_ID_PERSONA AND APS_PLANILLA.ID_ENTIDAD = P_ID_ENTIDAD),0,4) "Substring" into STRICT anho
			;

			SELECT SUBSTR((SELECT "MAX"("CONCAT"(ID_ANHO, ID_MES)) FROM APS_PLANILLA WHERE APS_PLANILLA.ID_CONTRATO = P_ID_CONTRATO
				AND APS_PLANILLA.ID_PERSONA = P_ID_PERSONA AND APS_PLANILLA.ID_ENTIDAD = P_ID_ENTIDAD),5,2) "Substring" INTO STRICT mes
			;

			SELECT NOM_CARGO INTO STRICT l_dato FROM APS_PLANILLA WHERE APS_PLANILLA.ID_CONTRATO = P_ID_CONTRATO
			AND APS_PLANILLA.ID_PERSONA = P_ID_PERSONA AND APS_PLANILLA.ID_ENTIDAD = P_ID_ENTIDAD AND ID_ANHO = anho AND ID_MES = mes;
     END IF;
   END IF;
	RETURN(l_dato);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
