CREATE OR REPLACE FUNCTION eliseo.fc_obtener_sp_tasa (P_ID_SISTEMAPENSION bigint, P_ID_ANHO bigint, P_ID_MES bigint) RETURNS varchar AS $body$
DECLARE

l_dato decimal(10,2):=0.00;


BEGIN
	SELECT SUM(TASA) INTO STRICT l_dato
	FROM APS_SISTEMA_PENSION_TASA 
	WHERE ID_SISTEMAPENSION = P_ID_SISTEMAPENSION AND ID_ANHO = P_ID_ANHO AND ID_MES = P_ID_MES;

	RETURN(l_dato);
END;
