CREATE OR REPLACE FUNCTION eliseo.fc_obt_name_entidad_depto (P_ID_ENTIDAD bigint, P_ID_DEPTO text) RETURNS varchar AS $body$
DECLARE

l_dato varchar(150):='';
l_contar integer:=0;
l_id_virtual bigint;


BEGIN

	IF (coalesce(P_ID_DEPTO::text, '') = '' OR P_ID_DEPTO = ' ' OR P_ID_DEPTO = 0)  THEN
			Select NOM_ENTIDAD INTO STRICT l_dato from VW_CONTA_ENTIDAD where ID_ENTIDAD = P_ID_ENTIDAD;
	ELSE
			Select VWCE.NOMBRE || ' - ' || VWCED.NOM_DEPARTAMENTO INTO STRICT l_dato from VW_CONTA_ENTIDAD VWCE
			INNER JOIN VW_CONTA_ENTIDAD_DEPTO VWCED ON VWCE.ID_ENTIDAD = VWCED.ID_ENTIDAD where VWCED.ID_ENTIDAD = P_ID_ENTIDAD AND VWCED.ID_DEPTO = P_ID_DEPTO;

	END IF;
	RETURN(l_dato);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
