CREATE OR REPLACE FUNCTION eliseo.fc_phono (P_ID_PERSONA bigint) RETURNS varchar AS $body$
DECLARE

    L_PHONO varchar(50);

BEGIN 
       
    SELECT
            coalesce(NUM_TELEFONO,'') INTO STRICT L_PHONO
    FROM PERSONA_TELEFONO
    WHERE ID_PERSONA = P_ID_PERSONA
    AND GTH = '1';
    RETURN(L_PHONO);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
