CREATE OR REPLACE FUNCTION eliseo.fc_psto_mes_nombre (P_ID_MES bigint,P_TIPO text) RETURNS varchar AS $body$
DECLARE

   l_mes_nombre varchar(30):='-';
   l_contar bigint;

BEGIN
	
   SELECT COUNT(*) INTO STRICT l_contar FROM CONTA_MES 
   WHERE ID_MES=P_ID_MES;
	
   IF l_contar=1 THEN
      SELECT CASE WHEN  P_TIPO='L' THEN NOMBRE ELSE SIGLAS END INTO STRICT l_mes_nombre FROM CONTA_MES 
      WHERE ID_MES=P_ID_MES;
   END IF;
   RETURN( l_mes_nombre );
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
