CREATE OR REPLACE FUNCTION eliseo.fc_psto_profesion (P_ID_CARGO_PROCESO bigint) RETURNS varchar AS $body$
DECLARE

l_dato varchar(3000):='';
l_nombre varchar(50);
p_conta integer;

CUR CURSOR FOR
SELECT COALESCE(b.NOMBRE_CORTO,b.NOMBRE)
FROM PSTO_PLLA_CARGO_PROCESO_PROF A,APS_PROFESION B
WHERE A.ID_PROFESION = B.ID_PROFESION
AND A.ID_CARGO_PROCESO=P_ID_CARGO_PROCESO
ORDER BY B.ID_PROFESION;



BEGIN
    p_conta:=1;
    open CUR;
        fetch CUR into l_nombre;
        while CUR%found loop
            if p_conta = 1 then
                l_dato := l_nombre;
            else
                l_dato := l_dato||'|'||l_nombre;
            end if;

            fetch CUR into l_nombre;
            p_conta:=p_conta+1;
        end loop;
	close CUR;	
	
	RETURN(l_dato);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
