CREATE OR REPLACE FUNCTION eliseo.fc_vehiculo (P_ID_VEHICULO bigint) RETURNS varchar AS $body$
DECLARE

    L_NOMBRE varchar(100);

BEGIN 
        /*
        1-RAZON SOCIAL
        2-NOMBRE COMERCIAL
        3-NOMBRE DENOMINACIONAL */
    SELECT
           PLACA||' - '||MODELO  INTO STRICT L_NOMBRE
    FROM PEDIDO_VEHICULO
    WHERE ID_VEHICULO = P_ID_VEHICULO;
    RETURN(L_NOMBRE);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
