CREATE OR REPLACE FUNCTION eliseo.ft_alumno_eap ( P_ID_PERSONA bigint ) RETURNS varchar AS $body$
DECLARE

l_contar bigint:=0;
l_eap varchar(500);

BEGIN
    SELECT count(*) into STRICT l_contar
    FROM DAVID.ACAD_ALUMNO_PLAN A, 
    DAVID.ACAD_PLAN_PROGRAMA B, 
    DAVID.ACAD_PROGRAMA_ESTUDIO C
    WHERE A.ID_PLAN_PROGRAMA=B.ID_PLAN_PROGRAMA
    AND B.ID_PROGRAMA_ESTUDIO=C.ID_PROGRAMA_ESTUDIO
    AND A.ID_PERSONA=P_ID_PERSONA
    AND A.ESTADO='1';

    if l_contar>0 then
        
        SELECT C.NOMBRE_CORTO into STRICT l_eap 
        FROM DAVID.ACAD_ALUMNO_PLAN A, 
        DAVID.ACAD_PLAN_PROGRAMA B, 
        DAVID.ACAD_PROGRAMA_ESTUDIO C
        WHERE A.ID_PLAN_PROGRAMA=B.ID_PLAN_PROGRAMA
        AND B.ID_PROGRAMA_ESTUDIO=C.ID_PROGRAMA_ESTUDIO
        AND A.ID_PERSONA=P_ID_PERSONA
        AND A.ESTADO='1' 
        ORDER BY C.ID_NIVEL_ENSENANZA,C.ID_MODALIDAD_ESTUDIO LIMIT 1;
    end if;
  RETURN l_eap;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
