CREATE OR REPLACE FUNCTION eliseo.ft_alumno_school_institucion ( P_ID_PERSONA bigint ) RETURNS varchar AS $body$
DECLARE

l_contar bigint:=0;
l_institucion varchar(500);

BEGIN

    SELECT COUNT(1) INTO STRICT l_contar
    FROM JOSE.SCHOOL_ALUMNO A
    INNER JOIN JOSE.SCHOOL_INSTITUCION B ON A.ID_INSTITUCION = B.ID_INSTITUCION
    WHERE A.ID_ALUMNO =P_ID_PERSONA;

    if l_contar>0 then

        SELECT B.NOMBRE INTO STRICT l_institucion
        FROM JOSE.SCHOOL_ALUMNO A
        INNER JOIN JOSE.SCHOOL_INSTITUCION B ON A.ID_INSTITUCION = B.ID_INSTITUCION
        WHERE A.ID_ALUMNO =P_ID_PERSONA  LIMIT 1;

    end if;
  RETURN l_institucion;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
