CREATE OR REPLACE FUNCTION eliseo.ft_alumno_school_nivelgrado ( P_ID_PERSONA bigint ) RETURNS varchar AS $body$
DECLARE

l_contar bigint:=0;
l_nivel_grado varchar(500);

BEGIN

    SELECT count(1) into STRICT l_contar
    FROM JOSE.SCHOOL_ALUMNO A
    INNER JOIN JOSE.SCHOOL_NIVEL B ON A.ID_NIVEL = B.ID_NIVEL
    INNER JOIN JOSE.SCHOOL_GRADO C ON A.ID_GRADO = C.ID_GRADO 
    WHERE A.ID_ALUMNO =P_ID_PERSONA;

    if l_contar>0 then

        SELECT B.NOMBRE || ' - ' || C.NOMBRE INTO STRICT l_nivel_grado
        FROM JOSE.SCHOOL_ALUMNO A
        INNER JOIN JOSE.SCHOOL_NIVEL B ON A.ID_NIVEL = B.ID_NIVEL
        INNER JOIN JOSE.SCHOOL_GRADO C ON A.ID_GRADO = C.ID_GRADO 
        WHERE A.ID_ALUMNO =P_ID_PERSONA  LIMIT 1;

    end if;
  RETURN l_nivel_grado;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
