CREATE OR REPLACE FUNCTION eliseo.tipo_doc_cliente ( v_id_persona integer) RETURNS varchar AS $body$
DECLARE

	p_tipo_doc	  varchar(800);


BEGIN

    select CASE WHEN id_tipodocumento=1 THEN  'DNI' WHEN id_tipodocumento=4 THEN  'CE' WHEN id_tipodocumento=6 THEN  'RUC' WHEN id_tipodocumento=7 THEN  'PASS' WHEN id_tipodocumento='11' THEN  'PN'  ELSE 'OTROS' END   into STRICT p_tipo_doc
    from moises.persona_natural
    where id_persona=v_id_persona;

return p_tipo_doc;

END;



