SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_accounting,public;




CREATE OR REPLACE FUNCTION eliseo.pkg_accounting_fc_cuenta_asiento ( p_id_entidad bigint, p_id_tipoorigen bigint, p_id_origen bigint, p_id_voucher bigint, p_dc text ) RETURNS varchar AS $body$
DECLARE

        l_cuenta     varchar(300);
        l_id_empresa bigint;
        l_dc         bigint;
        l_id_anho    bigint;

BEGIN
        SELECT
            a.id_empresa
        INTO STRICT l_id_empresa
        FROM
            conta_entidad a
        WHERE
            a.id_entidad = p_id_entidad;

        SELECT
            MAX(id_anho) AS id_anho
        INTO STRICT l_id_anho
        FROM
            conta_entidad_anho_config
        WHERE
                id_entidad = p_id_entidad
            AND activo = '1';

        IF p_dc = 'D' THEN
            SELECT
                string_agg(coalesce(a.cuenta
                            || ' - '
                            || MAX(b.id_cuentaempresarial), 'NO EXISTE CUENTA'), ','
                ORDER BY
                    a.cuenta
                ) AS cuenta
            INTO STRICT l_cuenta
            FROM
                conta_asiento     a
                LEFT JOIN conta_empresa_cta b ON a.cuenta = b.id_cuentaaasi
                                                 AND a.restriccion = b.id_restriccion
                                                 AND b.id_tipoplan = 1
                                                 AND b.id_anho = l_id_anho
                                                 AND b.id_empresa = l_id_empresa
            WHERE
                    a.id_tipoorigen = p_id_tipoorigen
                AND a.id_origen = p_id_origen
                AND a.voucher = p_id_voucher
                AND a.importe > 0
            GROUP BY
                a.cuenta;

        ELSE
            SELECT
                string_agg(coalesce(a.cuenta
                            || ' - '
                            || MAX(b.id_cuentaempresarial), 'NO EXISTE CUENTA'), ','
                ORDER BY
                    a.cuenta
                ) AS cuenta
            INTO STRICT l_cuenta
            FROM
                conta_asiento     a
                LEFT JOIN conta_empresa_cta b ON a.cuenta = b.id_cuentaaasi
                                                 AND a.restriccion = b.id_restriccion
                                                 AND b.id_tipoplan = 1
                                                 AND b.id_anho = l_id_anho
                                                 AND b.id_empresa = l_id_empresa
            WHERE
                    a.id_tipoorigen = p_id_tipoorigen
                AND a.id_origen = p_id_origen
                AND a.voucher = p_id_voucher
                AND a.importe < 0;

        END IF;

        RETURN( l_cuenta );
    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
