CREATE OR REPLACE FUNCTION eliseo.pkg_caja_fc_nombre_cta_bancaria (P_ID_CHEQUERA bigint) RETURNS varchar AS $body$
DECLARE

        L_BANCO varchar(255);

BEGIN
            SELECT 
            A.SIGLA||' - '||B.NOMBRE||' - '||B.CUENTA_CORRIENTE INTO STRICT L_BANCO
            FROM CAJA_ENTIDAD_FINANCIERA A JOIN CAJA_CUENTA_BANCARIA B
            ON A.ID_BANCO = B.ID_BANCO
            JOIN CAJA_CHEQUERA C
            ON B.ID_CTABANCARIA = C.ID_CTABANCARIA
            WHERE C.ID_CHEQUERA = P_ID_CHEQUERA;

            RETURN(L_BANCO);
    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
