SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_caja,public;




CREATE OR REPLACE FUNCTION eliseo.pkg_caja_fc_ruc (P_ID_PROVEEDOR bigint) RETURNS varchar AS $body$
DECLARE

        L_ID_RUC varchar(50);

BEGIN
            SELECT ID_RUC INTO STRICT L_ID_RUC FROM (
            SELECT ID_RUC AS ID_RUC
            FROM MOISES.VW_PERSONA_JURIDICA
            WHERE ID_PERSONA = P_ID_PROVEEDOR
            
UNION ALL

            SELECT NUM_DOCUMENTO AS ID_RUC
            FROM MOISES.VW_PERSONA_NATURAL
            WHERE ID_PERSONA = P_ID_PROVEEDOR
            AND ID_TIPODOCUMENTO=6
             ) X LIMIT 1;

            RETURN(L_ID_RUC);
    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
