CREATE OR REPLACE PROCEDURE eliseo.pkg_caja_sp_crear_detraccion_detalle (P_ID_DETRACCION bigint,P_ID_COMPRA bigint,P_ID_DINAMICA bigint,P_IMPORTE bigint,P_IMPORTE_ME bigint, P_ERROR out bigint, P_MSGERROR out text) AS $body$
DECLARE

        l_id_detdetalle bigint;
        l_contar bigint;
        l_error bigint:=0;
        l_msgerror varchar(200):='';
        l_importe_me decimal(10,2);
        L_NRO_OPERACION varchar(20);
        L_DETALLE varchar(255);
        L_ID_PROVEEDOR bigint;
        L_ID_COMPRA bigint;
        L_ID_SALDO bigint;

        
BEGIN
            l_error:=0;

            SELECT ID_PROVEEDOR,NRO_OPERACION INTO STRICT L_ID_PROVEEDOR,L_NRO_OPERACION
            FROM CAJA_DETRACCION
            WHERE ID_DETRACCION = P_ID_DETRACCION;

            SELECT 
                COUNT(1) INTO STRICT l_contar
            FROM COMPRA
            WHERE ID_COMPRA = P_ID_COMPRA AND ID_PROVEEDOR = L_ID_PROVEEDOR;

            IF l_contar > 0 THEN
                SELECT 
                    ID_ENTIDAD||'-'||ID_DEPTO||', Det.:'||L_NRO_OPERACION||' Doc:'||SERIE||'-'||NUMERO||' '||FC_NOMBRE_PERSONA(ID_PROVEEDOR) INTO STRICT L_DETALLE
                FROM COMPRA
                WHERE ID_COMPRA = P_ID_COMPRA AND ID_PROVEEDOR = L_ID_PROVEEDOR;
                L_ID_COMPRA := P_ID_COMPRA;
                L_ID_SALDO := NULL;
            ELSE
                SELECT
                    ID_ENTIDAD||'-'||ID_DEPTO||', Det.:'||L_NRO_OPERACION||' Doc:'||SERIE||'-'||NUMERO||' '||FC_NOMBRE_PERSONA(ID_PROVEEDOR) INTO STRICT L_DETALLE
                FROM COMPRA_SALDO
                WHERE ID_SALDO = P_ID_COMPRA AND ID_PROVEEDOR = L_ID_PROVEEDOR;
                L_ID_COMPRA := NULL;
                L_ID_SALDO := P_ID_COMPRA;
            END IF;

            -- SELECT coalesce(max(ID_DETDETALLE),0)+1 into l_id_detdetalle FROM CAJA_DETRACCION_COMPRA;
        
            if l_error=0 then
                --IF NVL(P_IMPORTE,0)+NVL(P_IMPORTE_ME,0) > 0 THEN
                    INSERT INTO CAJA_DETRACCION_COMPRA(
                    -- ID_DETDETALLE,
                    ID_DETRACCION,
                    ID_COMPRA,
                    ID_DINAMICA,
                    IMPORTE,
                    IMPORTE_ME,
                    DETALLE,
                    ID_SALDO
                    )values (
                    --l_id_detdetalle,
                    P_ID_DETRACCION,
                    L_ID_COMPRA,
                    P_ID_DINAMICA,
                    P_IMPORTE,
                    P_IMPORTE_ME,
                    L_DETALLE,
                    L_ID_SALDO
                    ) returning id_detdetalle INTO l_id_detdetalle;
                    l_msgerror:='Pago OK';
                /*ELSE
                    l_error := 1;
                    l_msgerror:='No existen Importes';
                END IF;*/
            end if;
        P_ERROR:=l_error;
        P_MSGERROR:=l_msgerror;
    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
