CREATE OR REPLACE PROCEDURE eliseo.pkg_caja_sp_eliminar_pago_deposito (P_ID_PAGO bigint) AS $body$
DECLARE

        L_ID_ORIGEN bigint;
        L_ID_TIPOORIGEN bigint;

        C_PAGO_GASTO CURSOR FOR	
            SELECT ID_TIPOORIGEN, ID_PGASTO
            FROM CAJA_PAGO_GASTO
            WHERE ID_PAGO=P_ID_PAGO;

        
BEGIN
        
        OPEN C_PAGO_GASTO;
        FETCH C_PAGO_GASTO INTO L_ID_TIPOORIGEN, L_ID_ORIGEN;

        WHILE C_PAGO_GASTO%FOUND LOOP

            DELETE FROM CONTA_ASIENTO WHERE ID_TIPOORIGEN = L_ID_TIPOORIGEN AND ID_ORIGEN=L_ID_ORIGEN;

            FETCH C_PAGO_GASTO INTO L_ID_TIPOORIGEN, L_ID_ORIGEN;
        END LOOP;
        CLOSE C_PAGO_GASTO;
        
        DELETE FROM CAJA_PAGO_GASTO where ID_PAGO = P_ID_PAGO;
        DELETE FROM CAJA_DEPOSITO where ID_PAGO = P_ID_PAGO;
        DELETE FROM CAJA_PAGO where ID_PAGO = P_ID_PAGO;

    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
