CREATE OR REPLACE PROCEDURE eliseo.pkg_caja_sp_eliminar_vale (P_ID_VALE bigint) AS $body$
DECLARE


        L_ID_TIPOORIGEN bigint;
        L_ID_ORIGEN bigint;

        
BEGIN
        
        SELECT ID_VALE, ID_TIPOORIGEN INTO STRICT L_ID_ORIGEN, L_ID_TIPOORIGEN FROM CAJA_VALE WHERE ID_VALE=P_ID_VALE;

        DELETE FROM CONTA_ASIENTO WHERE ID_TIPOORIGEN = L_ID_TIPOORIGEN AND ID_ORIGEN = L_ID_ORIGEN;

        DELETE FROM CAJA_VALE_ASIENTO WHERE ID_VALE=P_ID_VALE;

        DELETE FROM CAJA_VALE WHERE ID_VALE=P_ID_VALE;

    END;
$body$
