CREATE OR REPLACE PROCEDURE eliseo.pkg_caja_sp_rechazar_vale (P_ID_VALE bigint) AS $body$
DECLARE

        L_ID_PROCESO bigint := 5; --PROCESO DE GESTION DE VALES
        L_CANT bigint;

    
BEGIN
        
        SELECT COUNT(1) INTO STRICT L_CANT FROM PROCESS_RUN WHERE ID_OPERACION = P_ID_VALE AND ID_PROCESO = L_ID_PROCESO;
        IF L_CANT = 1 THEN
            UPDATE PROCESS_RUN SET ESTADO = '3' WHERE ID_OPERACION = P_ID_VALE AND ID_PROCESO = L_ID_PROCESO;

            UPDATE CAJA_VALE SET ESTADO = '3' WHERE ID_VALE = P_ID_VALE;
        END IF;
    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
