SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_finances_students,public;




CREATE OR REPLACE PROCEDURE eliseo.pkg_finances_students_sp_desc_bach_pregrado (P_ID_ALUMNO_CONTRATO bigint,P_ID_CRITERIO_SEMESTRE bigint,P_TIPO_ALUMNO text,P_ERROR OUT bigint,P_MSGERROR out text) AS $body$
DECLARE

    l_items bigint;
    l_contar bigint:=0;
    l_error bigint:=0;
    l_msgerror varchar(200):='';

    l_id_semestre_programa bigint;
    l_id_persona bigint;

    l_id_criterio bigint;
    l_importe decimal(10,2);
    l_dc varchar(5);
    l_dias decimal(10,2);
    l_nombre varchar(150);
    l_tipo_valor varchar(2);
    l_tipo_dscto varchar(3);
    l_id_semestre_act bigint;

BEGIN
    SELECT ID_SEMESTRE_PROGRAMA,ID_PERSONA 
    INTO STRICT l_id_semestre_programa,l_id_persona
    FROM DAVID.ACAD_ALUMNO_CONTRATO
    WHERE ID_ALUMNO_CONTRATO = P_ID_ALUMNO_CONTRATO;

     SELECT ID_SEMESTRE INTO STRICT l_id_semestre_act FROM DAVID.ACAD_SEMESTRE_PROGRAMA WHERE ID_SEMESTRE_PROGRAMA=l_id_semestre_programa;

    
    SELECT 
        ID_CRITERIO,
        DC,
        NOMBRE,
        TIPO_DSCTO,
        IMPORTE,
        TIPO_VALOR
        INTO STRICT
        l_id_criterio,
        l_dc,
        l_nombre,
        l_tipo_dscto,
        l_importe,
        l_tipo_valor
    FROM VW_MAT_CRITERIO_SEMESTRE
    WHERE ID_CRITERIO_SEMESTRE=P_ID_CRITERIO_SEMESTRE;

    
    select COUNT(*) INTO STRICT l_contar  
    from DAVID.Acad_Alumno_Plan aap, DAVID.Vw_Plan_Programa vpp 
    WHERE Vpp.Id_Plan_Programa=Aap.Id_Plan_Programa 
    and vpp.Id_facultad Between 8 and 12
    AND id_persona=l_id_persona 
    and graduado=1;


    IF l_contar>0 THEN

       SELECT COALESCE(MAX(ITEM),0)+1  INTO STRICT l_items FROM MAT_CONCEPTO_ASIGN_ALUM
       WHERE ID_ALUMNO_CONTRATO=P_ID_ALUMNO_CONTRATO;

       insert into MAT_CONCEPTO_ASIGN_ALUM(
        ID_ALUMNO_CONTRATO,
        ID_CRITERIO_SEMESTRE,
        ITEM,
        ID_SEMESTRE_PROGRAMA,
        DESCRIPCION,
        DC,
        TOTAL,
        TIPO_VALOR
        )VALUES (
        P_ID_ALUMNO_CONTRATO,
        P_ID_CRITERIO_SEMESTRE,
        l_items,
        l_id_semestre_programa,
        l_nombre,
        l_dc,
        l_importe,
        l_tipo_valor
        );

    END IF;

    P_ERROR:=l_error;
    P_MSGERROR:= l_msgerror;

  END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
