SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_finances_students,public;




CREATE OR REPLACE PROCEDURE eliseo.pkg_finances_students_sp_desc_cre_pra_cur_apza_ciclo (P_ID_ALUMNO_CONTRATO bigint,P_ID_CRITERIO_SEMESTRE bigint,P_TIPO_ALUMNO text,P_ERROR OUT bigint,P_MSGERROR out text) AS $body$
DECLARE

    l_items bigint;
    l_contar bigint;
    l_contar1 bigint;
    l_error bigint:=0;
    l_msgerror varchar(200):='';
    l_id_semestre_programa bigint;
    l_id_persona bigint;

    l_id_criterio	bigint;
    l_importe	decimal(10,2);
    l_tipo_proceso	varchar(2);
    l_tipo_valor	varchar(2);
    l_id_semestre bigint;
    l_nombre varchar(150);
    l_id_plan_programa bigint;
    l_id_plan bigint;
    l_dc varchar(1);
    l_id_semestre_act bigint;
    l_creditos decimal(10,2);
    l_costo decimal(10,2);
    l_ciclo bigint;

BEGIN
  
    SELECT ID_SEMESTRE_PROGRAMA,ID_PERSONA,ciclo
    INTO STRICT l_id_semestre_programa,l_id_persona,l_ciclo
    FROM DAVID.VW_ACAD_ALUMNO_CONTRATO
    WHERE ID_ALUMNO_CONTRATO = P_ID_ALUMNO_CONTRATO;

    SELECT ID_SEMESTRE INTO STRICT l_id_semestre_act FROM DAVID.ACAD_SEMESTRE_PROGRAMA WHERE ID_SEMESTRE_PROGRAMA=l_id_semestre_programa;

    SELECT 
      ID_CRITERIO_PROC,
      IMPORTE,
      TIPO_PROCESO,
      TIPO_VALOR,
      DC
      INTO STRICT
      l_id_criterio,
      l_importe,
      l_tipo_proceso,
      l_tipo_valor,
      l_dc
    FROM VW_MAT_CRITERIO_SEMESTRE
    WHERE ID_CRITERIO_SEMESTRE=P_ID_CRITERIO_SEMESTRE;

    DELETE FROM TT_CURSOS_X_PLAN;

    INSERT INTO TT_CURSOS_X_PLAN(
      ID_ALUMNO_CONTRATO,
      ID_SEMESTRE_PROGRAMA,
      ID_PROGRAMA_ESTUDIO,
      ID_PLAN,
      NOM_PLAN,
      NOM_ESCUELA,
      ID_SEMESTRE,
      CREDITOS
    )
    SELECT ID_ALUMNO_CONTRATO,ID_SEMESTRE_PROGRAMA,ID_PROGRAMA_ESTUDIO,ID_PLAN,NOMBRE,NOMBRE_ESCUELA,ID_SEMESTRE, sum(CREDITO)  as CREDITOS
    FROM VW_MAT_CURSOS_PLAN_ALUMNO
    WHERE ID_ALUMNO_CONTRATO=P_ID_ALUMNO_CONTRATO
    AND ID_CARGA_CURSO IN (
        SELECT
           A.ID_CARGA_CURSO
        FROM DAVID.ACAD_CURSO_ALUMNO A, DAVID.VW_ACAD_CARGA_ACADEMICA B
        WHERE A.ID_CARGA_CURSO=B.ID_CARGA_CURSO
        AND B.ORIGEN='O'
        AND A.ID_PERSONA=l_id_persona
        AND B.ID_SEMESTRE=l_id_semestre_act
        AND B.ID_CURSO_DETALLE  IN (  --LO QUE NO SE HA REPROBADO
          SELECT Y.CURSO_EQUIV
          FROM DAVID.ACAD_CURSO_ALUMNO X,
          DAVID.VW_CURSO_EQUIVALENTE Y
          WHERE X.ID_CURSO_DETALLE=Y.ID_CURSO_DETALLE
          AND X.ID_PERSONA=l_id_persona
          AND X.ID_TIPO_CONDICION=2
        ) 
    )  AND DESCUENTO_CREDITO='S' 
    group by ID_ALUMNO_CONTRATO,ID_SEMESTRE_PROGRAMA,ID_PROGRAMA_ESTUDIO,ID_PLAN,NOMBRE,NOMBRE_ESCUELA,ID_SEMESTRE
    ORDER BY CREDITOS;

    

    
    SELECT COUNT(*) INTO STRICT l_contar  FROM MAT_CICLO_COSTO a
    WHERE a.ciclo=l_ciclo
    AND A.ID_SEMESTRE_PROGRAMA=l_id_semestre_programa
    AND a.ID_CRITERIO=l_id_criterio;

    if l_contar=0 then
      l_error:=1;
      l_msgerror:='No esta asignado costo por ciclo de enseñanza';
--       goto salida_desc_cred_apzdo;
    end if;

    select count(*) into STRICT l_contar1 from TT_CURSOS_X_PLAN;

    if l_contar1=0 then
      l_error:=1;
      l_msgerror:='Cantidad de creidtos no existe';
--       goto salida_desc_cred_apzdo;
    end if;

    SELECT COUNT(*) INTO STRICT l_contar  FROM MAT_CICLO_COSTO a
    WHERE a.ciclo=l_ciclo
    AND A.ID_SEMESTRE_PROGRAMA=l_id_semestre_programa
    AND a.ID_CRITERIO=l_id_criterio
    AND coalesce(a.IMPORTE,0)<=0;

    if l_contar>0 then
      l_error:=1;
      l_msgerror:='Falta asignar costo por ciclo de enseñanza, importe cero';
--       goto salida_desc_cred_apzdo;
    end if;

     select coalesce(sum(CREDITOS),0) into STRICT l_creditos from TT_CURSOS_X_PLAN;

    SELECT  coalesce(IMPORTE,0) into STRICT  l_costo  FROM MAT_CICLO_COSTO a
    WHERE a.ciclo=l_ciclo
    AND A.ID_SEMESTRE_PROGRAMA=l_id_semestre_programa
    AND a.ID_CRITERIO=l_id_criterio;

    SELECT COALESCE(MAX(ITEM),0)+1  INTO STRICT l_items FROM MAT_CONCEPTO_ASIGN_ALUM
    WHERE ID_ALUMNO_CONTRATO=P_ID_ALUMNO_CONTRATO;

    
    insert into MAT_CONCEPTO_ASIGN_ALUM(
      ID_ALUMNO_CONTRATO,
      ID_CRITERIO_SEMESTRE,
      ITEM,
      ID_SEMESTRE_PROGRAMA,
      DESCRIPCION,
      DC,
      CANTIDAD,
      UNITARIO,
      TOTAL,
      TIPO_VALOR,
      ID_PLAN,
      APLAZADO,
      CREDITOS,
      UNITARIO_CRED
    )values ( 
    P_ID_ALUMNO_CONTRATO,
    P_ID_CRITERIO_SEMESTRE,
    l_items, 
    l_id_semestre_programa,
    l_nombre,
    l_dc,
    l_creditos,
    l_costo,
    l_creditos*l_costo,
    l_tipo_valor,
    null,
    'S',
    l_creditos,
    l_costo
    );
--     <<salida_desc_cred_apzdo>>
    P_ERROR:=l_error;
    P_MSGERROR:= l_msgerror;
  END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE eliseo.pkg_finances_students_sp_desc_cre_pra_cur_apza_ciclo (P_ID_ALUMNO_CONTRATO bigint,P_ID_CRITERIO_SEMESTRE bigint,P_TIPO_ALUMNO text,P_ERROR OUT bigint,P_MSGERROR out text) FROM PUBLIC;
