CREATE OR REPLACE PROCEDURE eliseo.pkg_finances_students_sp_eliminar_criterio_semestre (P_ID_CRITERIO_SEMESTRE bigint, P_ERROR OUT bigint,P_MSGERROR out text) AS $body$
DECLARE

      l_error bigint:=0;
      l_msgerror varchar(200):='';
      l_contar bigint;
      l_contar1 bigint;
      l_id_parent bigint;
      l_id_semestre_programa bigint;

BEGIN
      
      SELECT COUNT(*) INTO STRICT l_contar FROM  MAT_CONCEPTO_ASIGN_ALUM WHERE ID_CRITERIO_SEMESTRE=P_ID_CRITERIO_SEMESTRE;
      SELECT COUNT(*) INTO STRICT l_contar1 FROM  MAT_ALUMNO_CONTRATO_DET WHERE ID_CRITERIO_SEMESTRE=P_ID_CRITERIO_SEMESTRE;

      if l_contar=0 and l_contar1=0 then
        SELECT COUNT(*) INTO STRICT l_contar FROM  VW_MAT_CRITERIO_SEMESTRE WHERE ID_CRITERIO_SEMESTRE=P_ID_CRITERIO_SEMESTRE AND TIENE_HIJO>0;
        IF l_contar> 0 THEN
          SELECT id_criterio,id_semestre_programa INTO STRICT l_id_parent,l_id_semestre_programa FROM  VW_MAT_CRITERIO_SEMESTRE WHERE ID_CRITERIO_SEMESTRE=P_ID_CRITERIO_SEMESTRE AND TIENE_HIJO>0;

          
          delete from MAT_CRITERIO_SEMESTRE_ASIENTO where id_criterio_semestre in (
            SELECT id_criterio_semestre from VW_MAT_CRITERIO_SEMESTRE 
            where id_parent=l_id_parent 
            and ID_SEMESTRE_PROGRAMA=l_id_semestre_programa
          );

          delete from MAT_CRITERIO_SEMESTRE where id_criterio_semestre in (
            SELECT id_criterio_semestre from VW_MAT_CRITERIO_SEMESTRE 
            where id_parent=l_id_parent 
            and ID_SEMESTRE_PROGRAMA=l_id_semestre_programa
          );

           
        END IF;

        delete from MAT_CRITERIO_SEMESTRE_ASIENTO where id_criterio_semestre=P_ID_CRITERIO_SEMESTRE;

        delete from MAT_CRITERIO_SEMESTRE where id_criterio_semestre=P_ID_CRITERIO_SEMESTRE;
      else
        l_error:=1;
        l_msgerror:='Ya esta asociado al detalle contrato';
      end if;

      P_ERROR:=l_error;
      P_MSGERROR:= l_msgerror;
    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
