SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_finances_students,public;




CREATE OR REPLACE PROCEDURE eliseo.pkg_finances_students_sp_ense_regular_ciclo (P_ID_ALUMNO_CONTRATO bigint,P_ID_CRITERIO_SEMESTRE bigint,P_TIPO_ALUMNO text,P_ERROR OUT bigint,P_MSGERROR out text) AS $body$
DECLARE

    l_items bigint;
    l_contar bigint;
    l_contar1 bigint;
    l_error bigint:=0;
    l_msgerror varchar(200):='';
    l_id_semestre_programa bigint;
    l_id_persona bigint;

    l_id_criterio	bigint;
    l_importe	decimal(10,2);
    l_tipo_proceso	varchar(2);
    l_tipo_valor	varchar(2);
    l_nombre varchar(150);

    l_dc varchar(1);
    l_creditos decimal(10,2);
    l_costo  decimal(10,2);
    l_ciclo bigint;

BEGIN
  
    SELECT ID_SEMESTRE_PROGRAMA,ID_PERSONA,CICLO
    INTO STRICT l_id_semestre_programa,l_id_persona,l_ciclo
    FROM DAVID.VW_ACAD_ALUMNO_CONTRATO
    WHERE ID_ALUMNO_CONTRATO = P_ID_ALUMNO_CONTRATO;

    
    SELECT 
      ID_CRITERIO,
      IMPORTE,
      TIPO_PROCESO,
      TIPO_VALOR,
      DC,
      NOMBRE
      INTO STRICT
      l_id_criterio,
      l_importe,
      l_tipo_proceso,
      l_tipo_valor,
      l_dc,
      l_nombre
    FROM VW_MAT_CRITERIO_SEMESTRE
    WHERE ID_CRITERIO_SEMESTRE=P_ID_CRITERIO_SEMESTRE;

    DELETE FROM TT_CURSOS_X_PLAN;

    INSERT INTO TT_CURSOS_X_PLAN(
      ID_ALUMNO_CONTRATO,
      ID_SEMESTRE_PROGRAMA,
      ID_PROGRAMA_ESTUDIO,
      ID_PLAN,
      NOM_PLAN,
      NOM_ESCUELA,
      ID_SEMESTRE,
      CREDITOS
    )
    SELECT ID_ALUMNO_CONTRATO,ID_SEMESTRE_PROGRAMA,ID_PROGRAMA_ESTUDIO,ID_PLAN,NOMBRE,NOMBRE_ESCUELA,ID_SEMESTRE, sum(CREDITO)  as CREDITOS
    FROM VW_MAT_CURSOS_PLAN_ALUMNO
    WHERE ID_ALUMNO_CONTRATO=P_ID_ALUMNO_CONTRATO
    group by ID_ALUMNO_CONTRATO,ID_SEMESTRE_PROGRAMA,ID_PROGRAMA_ESTUDIO,ID_PLAN,NOMBRE,NOMBRE_ESCUELA,ID_SEMESTRE
    ORDER BY CREDITOS;

       
     select coalesce(sum(CREDITOS),0) into STRICT l_creditos from TT_CURSOS_X_PLAN;

    if l_creditos=0 then
      l_error:=1;
      l_msgerror:='Cantidad de créditos igual a cero';
--       goto salida_enseregular;
    end if;

    SELECT  COUNT(*) INTO STRICT l_contar  FROM MAT_CICLO_COSTO a
    WHERE a.ciclo=l_ciclo
    AND A.ID_SEMESTRE_PROGRAMA=l_id_semestre_programa
    AND a.ID_CRITERIO=l_id_criterio;

    if l_contar=0 then
      l_error:=1;
      l_msgerror:='Falta asignar costo por ciclo de enseñanza';
--       goto salida_enseregular;
    end if;

    SELECT COUNT(*) INTO STRICT l_contar  FROM MAT_CICLO_COSTO a
    WHERE a.ciclo=l_ciclo
    AND A.ID_SEMESTRE_PROGRAMA=l_id_semestre_programa
    AND a.ID_CRITERIO=l_id_criterio
    AND coalesce(a.IMPORTE,0)<=0;

    if l_contar>0 then
      l_error:=1;
      l_msgerror:='Falta asignar costo de enseñanza, importe cero';
--       goto salida_enseregular;
    end if;

     SELECT  coalesce(IMPORTE,0) into STRICT  l_costo  FROM MAT_CICLO_COSTO a
    WHERE a.ciclo=l_ciclo
    AND A.ID_SEMESTRE_PROGRAMA=l_id_semestre_programa
    AND a.ID_CRITERIO=l_id_criterio;

    SELECT COALESCE(MAX(ITEM),0)+1  INTO STRICT l_items FROM MAT_CONCEPTO_ASIGN_ALUM
    WHERE ID_ALUMNO_CONTRATO=P_ID_ALUMNO_CONTRATO;

    
    
    insert into MAT_CONCEPTO_ASIGN_ALUM(
      ID_ALUMNO_CONTRATO,
      ID_CRITERIO_SEMESTRE,
      ITEM,
      ID_SEMESTRE_PROGRAMA,
      DESCRIPCION,
      DC,
      CANTIDAD,
      UNITARIO,
      TOTAL,
      TIPO_VALOR,
      ID_PLAN
     )VALUES (
    P_ID_ALUMNO_CONTRATO,
    P_ID_CRITERIO_SEMESTRE,
    l_items, 
    l_id_semestre_programa,
    l_nombre,
    l_dc,
    l_creditos,
    l_costo,
    l_creditos*l_costo,
    l_tipo_valor,
    null
    );

--     <<salida_enseregular>>
    P_ERROR:=l_error;
    P_MSGERROR:= l_msgerror;
  END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE eliseo.pkg_finances_students_sp_ense_regular_ciclo (P_ID_ALUMNO_CONTRATO bigint,P_ID_CRITERIO_SEMESTRE bigint,P_TIPO_ALUMNO text,P_ERROR OUT bigint,P_MSGERROR out text) FROM PUBLIC;
