CREATE OR REPLACE FUNCTION eliseo.pkg_inventories_fc_articulo_stock (P_ID_ALMACEN bigint,P_ID_ARTICULO bigint,P_ID_ANHO bigint) RETURNS varchar AS $body$
DECLARE

    P_STOCK decimal(10,2);

BEGIN
        SELECT A.STOCK_ACTUAL  INTO STRICT P_STOCK
        FROM INVENTARIO_ALMACEN_ARTICULO A
        WHERE A.ID_ALMACEN = P_ID_ALMACEN
        AND A.ID_ARTICULO = P_ID_ARTICULO
        AND A.ID_ANHO = P_ID_ANHO;
        RETURN(P_STOCK);
    END;
$body$
