SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_pstoplanilla,public;




CREATE OR REPLACE PROCEDURE eliseo.pkg_pstoplanilla_sp_eliminar_planilla_dist (P_ID_PLLA_PLANILLA_DIST bigint) AS $body$
DECLARE


          l_error bigint:=0;
          l_msgerror varchar(200):='';

          l_imp_aso decimal(10,2);
          l_imp_queda decimal(10,2);



      
          l_psto_planilla bigint;

        
BEGIN
      
        SELECT ID_PSTO_PLANILLA into STRICT l_psto_planilla FROM PSTO_PLLA_PLANILLA_DIST where ID_PLLA_PLANILLA_DIST=P_ID_PLLA_PLANILLA_DIST;

        
        DELETE from PSTO_PLLA_PLANILLA_DIST_DET  where ID_PLLA_PLANILLA_DIST=P_ID_PLLA_PLANILLA_DIST;

        DELETE FROM PSTO_PLLA_PLANILLA_DIST where ID_PLLA_PLANILLA_DIST=P_ID_PLLA_PLANILLA_DIST;

        
        
        SELECT COALESCE(SUM(case when TIPO='0' then PORCENTAJE else 0 end),0) 
        INTO STRICT l_imp_aso FROM PSTO_PLLA_PLANILLA_DIST
        WHERE ID_PSTO_PLANILLA=l_psto_planilla;

        
        l_imp_queda:=100-l_imp_aso;

       
        UPDATE PSTO_PLLA_PLANILLA_DIST SET PORCENTAJE=l_imp_queda
        WHERE ID_PSTO_PLANILLA=l_psto_planilla 
        AND TIPO='1';

                   
        CALL pkg_pstoplanilla_sp_generar_planilla_dist_det(l_psto_planilla,l_error,l_msgerror);
               

        END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
