SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_purchases,public;




CREATE OR REPLACE PROCEDURE eliseo.pkg_purchases_sp_delete_ajuste (P_ID_AJUSTE bigint,P_ERROR OUT bigint,P_MSG OUT text) AS $body$
DECLARE

        L_ID_VOUCHER bigint;	
        L_TIPOORIGEN bigint :=4; --COMPRA TRANSFERENCIA O AJUS 
        L_ACTIVO varchar(1) :='N';

        
BEGIN
            P_ERROR :=0;
            SELECT ID_VOUCHER INTO STRICT L_ID_VOUCHER 
            FROM COMPRA_AJUSTE
            WHERE ID_AJUSTE = P_ID_AJUSTE;

            SELECT ACTIVO INTO STRICT L_ACTIVO
            FROM CONTA_VOUCHER 
            WHERE ID_VOUCHER = L_ID_VOUCHER;

            IF L_ACTIVO = 'S' THEN 
                CALL pkg_accounting_sp_delete_all_conta_asiento(L_TIPOORIGEN,P_ID_AJUSTE,L_ID_VOUCHER);

                DELETE FROM COMPRA_AJUSTE 
                WHERE ID_AJUSTE = P_ID_AJUSTE;

                P_ERROR:=0;
                P_MSG :='OK';
            ELSE
                P_ERROR:=1;
                P_MSG :='ERROR: Asiento Contabilizado, no se puede Eliminar';
            END IF;
    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
