SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_purchases,public;




CREATE OR REPLACE PROCEDURE eliseo.pkg_purchases_sp_delete_compra (P_ID_PEDIDO bigint,P_ERROR OUT bigint,P_MSG OUT text) AS $body$
DECLARE

        L_ID_COMPRA bigint;	
        L_CANT bigint :=0;
        L_ESTADO varchar(1) :='0';

        
BEGIN
            P_ERROR :=0;
            SELECT ID_COMPRA INTO STRICT L_ID_COMPRA FROM PEDIDO_COMPRA
            WHERE ID_PEDIDO = P_ID_PEDIDO AND ESTADO <> '1' AND (ID_COMPRA IS NOT NULL AND ID_COMPRA::text <> '');

            SELECT ESTADO INTO STRICT L_ESTADO FROM COMPRA
            WHERE ID_COMPRA = L_ID_COMPRA;

            IF L_ESTADO = '0' THEN
                SELECT COUNT(1) INTO STRICT L_CANT FROM COMPRA_DETALLE
                WHERE ID_COMPRA = L_ID_COMPRA;
                IF L_CANT > 0 THEN
                    DELETE FROM COMPRA_DETALLE
                    WHERE ID_COMPRA = L_ID_COMPRA;
                END IF;
                SELECT COUNT(1) INTO STRICT L_CANT FROM COMPRA_ASIENTO
                WHERE ID_COMPRA = L_ID_COMPRA;
                IF L_CANT > 0 THEN
                    DELETE FROM COMPRA_ASIENTO WHERE ID_COMPRA = L_ID_COMPRA;
                END IF;
                UPDATE PEDIDO_COMPRA SET ID_COMPRA  = NULL WHERE ID_PEDIDO = P_ID_PEDIDO;
                DELETE FROM COMPRA WHERE ID_COMPRA = L_ID_COMPRA AND ESTADO <> '1';
                P_ERROR:=0;
                P_MSG :='OK';
            ELSE
                P_ERROR:=1;
                P_MSG :='ERROR: No se ha Eliminado la Compra';
            END IF;
    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
