SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_purchases_orders,public;




CREATE OR REPLACE FUNCTION eliseo.pkg_purchases_orders_fc_fecha_paso (P_ID_DEXEC bigint,P_CODIGO text) RETURNS varchar AS $body$
DECLARE

        L_ID_EXECUTE bigint;
        FECHA varchar(100);

BEGIN
        SELECT DISTINCT ID_EXECUTE INTO STRICT L_ID_EXECUTE FROM LAMB_PROCESS_EXEC_PASO WHERE ID_DEXEC = P_ID_DEXEC;
        SELECT FECHA INTO STRICT FECHA FROM (
        	SELECT
	                A.FECHA
	        FROM LAMB_PROCESS_EXEC_PASO A JOIN LAMB_PROCESS_PASO B ON A.ID_PASO_ACTUAL = B.ID_PASO AND A.ESTADO <> 3 
	        WHERE A.ID_EXECUTE = L_ID_EXECUTE
	        AND B.CODIGO = P_CODIGO
	        ORDER BY A.ID_DEXEC DESC
        ) alias0 LIMIT 1;

            
        RETURN(FECHA);
    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
