SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_purchases_orders,public;




CREATE OR REPLACE PROCEDURE eliseo.pkg_purchases_orders_sp_delete_pre_provision (P_ID_ENTIDAD bigint,P_ID_DEPTO text,P_ID_PEDIDO bigint,P_ERROR OUT bigint,P_MSG OUT text) AS $body$
DECLARE

        L_ID_PROCESO bigint;
        L_ID_PCOMPRA bigint;
        L_ID_COMPRA bigint;
        L_ID_REGISTRO bigint;
        L_ESTADO varchar(1);
        L_CANT bigint;
        L_ID_EXECUTE integer;
        L_ID_DEXEC integer;

        L_ERROR bigint := 0;
        L_MSG varchar(300) := 'Ok';

        
BEGIN
        
            SELECT ID_DEXEC INTO STRICT L_ID_DEXEC FROM PEDIDO_REGISTRO WHERE ID_PEDIDO = P_ID_PEDIDO;

            SELECT DISTINCT ID_EXECUTE INTO STRICT L_ID_EXECUTE FROM LAMB_PROCESS_EXEC_PASO WHERE ID_DEXEC = L_ID_DEXEC;

            SELECT 
                    COUNT(1) INTO STRICT L_CANT
            FROM PEDIDO_COMPRA
            WHERE ID_PEDIDO = P_ID_PEDIDO;

            IF L_CANT > 0 THEN
                SELECT 
                    COUNT(1) INTO STRICT L_CANT 
                FROM PEDIDO_COMPRA
                WHERE ID_PEDIDO = P_ID_PEDIDO;
                IF L_CANT = 1 THEN
                    SELECT
                    ID_PCOMPRA,ID_COMPRA INTO STRICT L_ID_PCOMPRA,L_ID_COMPRA
                    FROM PEDIDO_COMPRA
                    WHERE ID_PEDIDO = P_ID_PEDIDO;
                ELSE
                    L_ERROR :=1;
                    L_MSG := 'ERROR: No se puede eliminar, Eliminar la Provision Temporal';
--                     goto salida_rapida;
                END IF;

            END IF;

            IF coalesce(L_ID_COMPRA::text, '') = '' THEN 
                L_CANT :=0;
            ELSE
                SELECT COUNT(1) L_CANT INTO STRICT L_ID_COMPRA
                FROM COMPRA
                WHERE ID_COMPRA = L_ID_COMPRA;
            END IF;

            IF L_CANT <> 0 THEN  --ELIMINAR TODO
                L_ERROR :=1;
                L_MSG := 'ERROR: No se puede eliminar, Eliminar la Provision';
--                 goto salida_rapida;
            END IF;

            
            
            --PEDIDO_COMPRA
            DELETE FROM PEDIDO_COMPRA_DETALLE
            WHERE ID_PCOMPRA = L_ID_PCOMPRA;

            DELETE FROM PROYECTO_COMPRA
            WHERE ID_PCOMPRA = L_ID_PCOMPRA;

            DELETE FROM PEDIDO_COMPRA_ITEM
            WHERE ID_PCOMPRA = L_ID_PCOMPRA;

            DELETE FROM PEDIDO_COMPRA
            WHERE ID_PCOMPRA = L_ID_PCOMPRA;

            SELECT COUNT(1) INTO STRICT L_CANT FROM PEDIDO_REQUERIMIENTO WHERE ID_REQUERIMIENTO = P_ID_PEDIDO;
            IF L_CANT > 0 THEN
                DELETE FROM PEDIDO_REQUERIMIENTO WHERE ID_REQUERIMIENTO = P_ID_PEDIDO;
            END IF;

            --PEDIDO
            DELETE FROM PEDIDO_DETALLE
            WHERE ID_PEDIDO = P_ID_PEDIDO;

            DELETE FROM PEDIDO_COTIZACION
            WHERE ID_PEDIDO = P_ID_PEDIDO;

            SELECT COUNT(1) INTO STRICT L_CANT
            FROM COMPRA_ORDEN
            WHERE ID_PEDIDO = P_ID_PEDIDO;

            IF L_CANT > 0 THEN
                DELETE FROM COMPRA_ORDEN_DETALLE
                WHERE ID_ORDEN IN (SELECT ID_ORDEN FROM COMPRA_ORDEN WHERE ID_PEDIDO = P_ID_PEDIDO);

                DELETE FROM COMPRA_ORDEN
                WHERE ID_PEDIDO = P_ID_PEDIDO;
            END IF;

            DELETE FROM PEDIDO_FILE
            WHERE ID_PEDIDO = P_ID_PEDIDO;

            DELETE FROM PEDIDO_ASIENTO
            WHERE ID_PEDIDO = P_ID_PEDIDO;

            DELETE FROM PEDIDO_REGISTRO
            WHERE ID_PEDIDO = P_ID_PEDIDO;

            
            --PROCESS
            DELETE FROM LAMB_PROCESS_EXEC_PASO WHERE ID_EXECUTE = L_ID_EXECUTE;
            DELETE FROM LAMB_PROCESS_EXECUTE WHERE ID_EXECUTE = L_ID_EXECUTE;

--             <<salida_rapida>>
            P_ERROR := L_ERROR;
            P_MSG := L_MSG;
        EXCEPTION
            WHEN OTHERS THEN
            P_MSG := 'ERROR AL ELIMINAR'||SQLSTATE||' -ERROR- '||SQLERRM;
            P_ERROR :=1;

    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
