SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_rvie_rce,public;




CREATE OR REPLACE PROCEDURE eliseo.pkg_rvie_rce_sp_crear_ventas_preliminar (P_ID_EMPRESA bigint,P_PERIODO text,P_TIPO text, P_ID_USER bigint,P_ERROR OUT bigint,P_MSGERROR out text) AS $body$
DECLARE

      l_error bigint:=0;
      l_msgerror varchar(200):='ok';
      l_contar bigint;
      l_ruc  varchar(20);
      l_orden bigint;
      l_id bigint:=0;
      l_corr bigint:=0;

BEGIN
    
      select id_ruc into STRICT l_ruc from conta_empresa where id_empresa=P_ID_EMPRESA;

      select count(1) into STRICT l_contar from SIRE_VENTAS_PRELI where id_empresa=P_ID_EMPRESA and periodo=P_PERIODO and ID_ESTADO_PREL_RVIE not in ('00');

      if l_contar>0 then
        l_error:=1;
        l_msgerror:='Primero elimine los registros anteriores del mes';
--         goto salida;
      end if;

     select coalesce(max(ID_VENTAS_PRELI),0) +1 into STRICT l_id from SIRE_VENTAS_PRELI;

     if P_TIPO='R' then
      select coalesce(max(CORRELATIVO),0) + 1 into STRICT l_corr from SIRE_VENTAS_PRELI
      where id_empresa=P_ID_EMPRESA
      and periodo=P_PERIODO;
     end if;

     INSERT INTO SIRE_VENTAS_PRELI(
      ID_VENTAS_PRELI,
      ID_EMPRESA,
      PERIODO,
      TIPO,
      ID_ESTADO_PREL_RVIE,
      CORRELATIVO,
      ID_USER_REG,
      FECHA_REG
      )VALUES (
        l_id,
        P_ID_EMPRESA,
        P_PERIODO,
        P_TIPO,
        '01',
        l_corr,
        P_ID_USER,
        clock_timestamp()
      );

      select coalesce(max(coalesce(orden,0)),0) into STRICT l_orden from SIRE_VENTAS_PRELI_DET 
      where ID_VENTAS_PRELI in (
        SELECT ID_VENTAS_PRELI from SIRE_VENTAS_PRELI where id_empresa=P_ID_EMPRESA and periodo=P_PERIODO
      );

      if P_TIPO='P' then
        insert into SIRE_VENTAS_PRELI_DET(
          ID_VENTAS_PRELI_DET,
          ID_VENTAS_PRELI,
          ORDEN,
          TIPO,
          FEMISION,
          FVCTOPAGO,
          TIPOCOMP,
          SERIE,
          NRODOCINICIAL,
          NRODOFINAL,
          TIPODOC,
          NRODOCUMENTO,
          RAZONSOCIAL,
          VALFACEXPO,
          BIGRAVADA,
          DSCTOBI,
          IGVIPM,
          DSCTOIGVIPM,
          MTOEXONERADO,
          MTOINAFECTO,
          ISC,
          BIGRAVIVAP,
          IVAP,
          ICBPER,
          OTROSTRIBUTOS,
          TOTALCP,
          MONEDA,
          TIPOCAMBIO,
          FEMISIONDOCMOD,
          TIPOCPMOD,
          SERIECPMOD,
          NROCPMOD,
          IDPROYOPERATRI,
          TIPODENOTA,
          ESTCOMP,
          VALORFOBEMBO,
          VALOROPGRAT,
          TIPOOPERACION,
          DAMCP,
          CLU,
          CARSUNAT,
          VIGENCIA,
          EDITADO,
          ID_USER_REG,
          FECHA_REG
        )
        SELECT 
        P_ID_EMPRESA::text||PERIODO|| to_char((row_number() OVER ( ORDER BY ID_VENTAS_SUNAT ASC )) + l_orden) as ID_VENTAS_PRELI_DET,
        l_id,
        (row_number() OVER ( ORDER BY ID_VENTAS_SUNAT ASC )) + l_orden,
        'P',
        FEMISION,
        FVCTOPAGO,
        TIPOCOMP,
        SERIE,
        NRODOCINICIAL,
        NRODOFINAL,
        TIPODOC,
        NRODOCUMENTO,
        RAZONSOCIAL,
        VALFACEXPO,
        BIGRAVADA,
        DSCTOBI,
        IGVIPM,
        DSCTOIGVIPM,
        MTOEXONERADO,
        MTOINAFECTO,
        ISC,
        BIGRAVIVAP,
        IVAP,
        ICBPER,
        OTROSTRIBUTOS,
        TOTALCP,
        MONEDA,
        TIPOCAMBIO,
        FEMISIONDOCMOD,
        TIPOCPMOD,
        SERIECPMOD,
        NROCPMOD,
        IDPROYOPERATRI,
        TIPODENOTA,
        ESTCOMP,
        VALORFOBEMBO,
        VALOROPGRAT,
        TIPOOPERACION,
        DAMCP,
        CLU,
        CARSUNAT,
        1,
        0,
        P_ID_USER,
        clock_timestamp()
        from SIRE_VENTAS_SUNAT 
        where id_empresa=P_ID_EMPRESA 
        and periodo=P_PERIODO
        order by TIPOCOMP,SERIE,NRODOCINICIAL;
    else
      insert into SIRE_VENTAS_PRELI_DET(
          ID_VENTAS_PRELI_DET,
          ID_VENTAS_PRELI,
          ORDEN,
          TIPO,
          FEMISION,
          FVCTOPAGO,
          TIPOCOMP,
          SERIE,
          NRODOCINICIAL,
          NRODOFINAL,
          TIPODOC,
          NRODOCUMENTO,
          RAZONSOCIAL,
          VALFACEXPO,
          BIGRAVADA,
          DSCTOBI,
          IGVIPM,
          DSCTOIGVIPM,
          MTOEXONERADO,
          MTOINAFECTO,
          ISC,
          BIGRAVIVAP,
          IVAP,
          ICBPER,
          OTROSTRIBUTOS,
          TOTALCP,
          MONEDA,
          TIPOCAMBIO,
          FEMISIONDOCMOD,
          TIPOCPMOD,
          SERIECPMOD,
          NROCPMOD,
          IDPROYOPERATRI,
          TIPODENOTA,
          ESTCOMP,
          VALORFOBEMBO,
          VALOROPGRAT,
          TIPOOPERACION,
          DAMCP,
          CLU,
          CARSUNAT,
          VIGENCIA,
          EDITADO,
          ID_USER_REG,
          FECHA_REG
        )
        SELECT
        P_ID_EMPRESA::text||a.PERIODO|| to_char((row_number() OVER ( ORDER BY a.ID_VENTAS_SUNAT ASC )) + l_orden) as ID_VENTAS_PRELI_DET,
        l_id,
        (row_number() OVER ( ORDER BY a.ID_VENTAS_SUNAT ASC )) + l_orden,
        'P',
        a.FEMISION,
        a.FVCTOPAGO,
        a.TIPOCOMP,
        a.SERIE,
        a.NRODOCINICIAL,
        a.NRODOFINAL,
        a.TIPODOC,
        a.NRODOCUMENTO,
        a.RAZONSOCIAL,
        a.VALFACEXPO,
        a.BIGRAVADA,
        a.DSCTOBI,
        a.IGVIPM,
        a.DSCTOIGVIPM,
        a.MTOEXONERADO,
        a.MTOINAFECTO,
        a.ISC,
        a.BIGRAVIVAP,
        a.IVAP,
        a.ICBPER,
        a.OTROSTRIBUTOS,
        a.TOTALCP,
        a.MONEDA,
        a.TIPOCAMBIO,
        a.FEMISIONDOCMOD,
        a.TIPOCPMOD,
        a.SERIECPMOD,
        a.NROCPMOD,
        a.IDPROYOPERATRI,
        a.TIPODENOTA,
        a.ESTCOMP,
        a.VALORFOBEMBO,
        a.VALOROPGRAT,
        a.TIPOOPERACION,
        a.DAMCP,
        a.CLU,
        a.CARSUNAT,
        1,
        0,
        P_ID_USER,
        clock_timestamp()
        from SIRE_VENTAS_SUNAT a
        where a.id_empresa=P_ID_EMPRESA 
        and a.periodo=P_PERIODO
        and a.CARSUNAT in (
          /*select w.CARSUNAT from SIRE_VENTAS_CONTRIBUY w
          where w.id_empresa=P_ID_EMPRESA
          and w.periodo=P_PERIODO
          and w.TIPODOC=a.TIPODOC
          and w.NRODOCUMENTO=a.NRODOCUMENTO
          and lower(w.RAZONSOCIAL)=lower(a.RAZONSOCIAL)
          union*/
          SELECT w.CARSUNAT from SIRE_VENTAS_CONTRIBUY w
          where w.id_empresa=P_ID_EMPRESA
          and w.periodo=P_PERIODO
          and coalesce(w.VALFACEXPO,0)= coalesce(a.VALFACEXPO,0)
          and coalesce(w.BIGRAVADA,0)= coalesce(a.BIGRAVADA,0)
          and coalesce(w.DSCTOBI,0)= coalesce(a.DSCTOBI,0)
          and coalesce(w.IGVIPM,0)= coalesce(a.IGVIPM,0)
          and coalesce(w.DSCTOIGVIPM,0)= coalesce(a.DSCTOIGVIPM,0)
          and coalesce(w.MTOEXONERADO,0)= coalesce(a.MTOEXONERADO,0)
          and coalesce(w.MTOINAFECTO,0)= coalesce(a.MTOINAFECTO,0)
          and coalesce(w.ISC,0)= coalesce(a.ISC,0)
          and coalesce(w.BIGRAVIVAP,0)= coalesce(a.BIGRAVIVAP,0)
          and coalesce(w.IVAP,0)= coalesce(a.IVAP,0)
          and coalesce(w.ICBPER,0)= coalesce(a.ICBPER,0)
          and coalesce(w.OTROSTRIBUTOS,0)= coalesce(a.OTROSTRIBUTOS,0)
          and coalesce(w.TOTALCP,0)= coalesce(a.TOTALCP,0) 
        ) 
        order by a.TIPOCOMP,a.SERIE,a.NRODOCINICIAL;

      select coalesce(max(coalesce(orden,0)),0) into STRICT l_orden from SIRE_VENTAS_PRELI_DET 
      where ID_VENTAS_PRELI in (
        SELECT ID_VENTAS_PRELI from SIRE_VENTAS_PRELI where id_empresa=P_ID_EMPRESA and periodo=P_PERIODO
      );

      insert into SIRE_VENTAS_PRELI_DET(
          ID_VENTAS_PRELI_DET,
          ID_VENTAS_PRELI,
          ORDEN,
          TIPO,
          FEMISION,
          FVCTOPAGO,
          TIPOCOMP,
          SERIE,
          NRODOCINICIAL,
          NRODOFINAL,
          TIPODOC,
          NRODOCUMENTO,
          RAZONSOCIAL,
          VALFACEXPO,
          BIGRAVADA,
          DSCTOBI,
          IGVIPM,
          DSCTOIGVIPM,
          MTOEXONERADO,
          MTOINAFECTO,
          ISC,
          BIGRAVIVAP,
          IVAP,
          ICBPER,
          OTROSTRIBUTOS,
          TOTALCP,
          MONEDA,
          TIPOCAMBIO,
          FEMISIONDOCMOD,
          TIPOCPMOD,
          SERIECPMOD,
          NROCPMOD,
          IDPROYOPERATRI,
          TIPODENOTA,
          ESTCOMP,
          VALORFOBEMBO,
          VALOROPGRAT,
          TIPOOPERACION,
          DAMCP,
          CLU,
          CARSUNAT,
          VIGENCIA,
          EDITADO,
          ID_USER_REG,
          FECHA_REG
        )
        SELECT 
        P_ID_EMPRESA::text||a.PERIODO|| to_char((row_number() OVER ( ORDER BY a.ID_VENTAS_SUNAT ASC )) + l_orden) as ID_VENTAS_PRELI_DET,
        l_id,
        (row_number() OVER ( ORDER BY a.ID_VENTAS_SUNAT ASC )) + l_orden,
        'P',
        a.FEMISION,
        a.FVCTOPAGO,
        a.TIPOCOMP,
        a.SERIE,
        a.NRODOCINICIAL,
        a.NRODOFINAL,
        a.TIPODOC,
        a.NRODOCUMENTO,
        a.RAZONSOCIAL,
        a.VALFACEXPO,
        a.BIGRAVADA,
        a.DSCTOBI,
        a.IGVIPM,
        a.DSCTOIGVIPM,
        a.MTOEXONERADO,
        a.MTOINAFECTO,
        a.ISC,
        a.BIGRAVIVAP,
        a.IVAP,
        a.ICBPER,
        a.OTROSTRIBUTOS,
        a.TOTALCP,
        a.MONEDA,
        a.TIPOCAMBIO,
        a.FEMISIONDOCMOD,
        a.TIPOCPMOD,
        a.SERIECPMOD,
        a.NROCPMOD,
        a.IDPROYOPERATRI,
        a.TIPODENOTA,
        a.ESTCOMP,
        a.VALORFOBEMBO,
        a.VALOROPGRAT,
        a.TIPOOPERACION,
        a.DAMCP,
        a.CLU,
        a.CARSUNAT,
        1,
        0,
        P_ID_USER,
        clock_timestamp()
        from SIRE_VENTAS_SUNAT a
        where a.id_empresa=P_ID_EMPRESA 
        and a.periodo=P_PERIODO
        and a.CARSUNAT not in (
          SELECT w.CARSUNAT from SIRE_VENTAS_CONTRIBUY w
          where w.id_empresa=P_ID_EMPRESA
          and w.periodo=P_PERIODO
        ) 
        order by a.TIPOCOMP,a.SERIE,a.NRODOCINICIAL;

    end if;
--     <<salida>>
    P_ERROR:=l_error;
    P_MSGERROR:= l_msgerror;

    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE eliseo.pkg_rvie_rce_sp_crear_ventas_preliminar (P_ID_EMPRESA bigint,P_PERIODO text,P_TIPO text, P_ID_USER bigint,P_ERROR OUT bigint,P_MSGERROR out text) FROM PUBLIC;
