SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_sales,public;




CREATE OR REPLACE FUNCTION eliseo.pkg_sales_fc_credito_personal (P_ID_PERSONA bigint) RETURNS varchar AS $body$
DECLARE

    L_CREDITO varchar(1) :='N';

BEGIN
        SELECT CASE WHEN CREDITO=1 THEN 'S'  ELSE 'N' END  INTO STRICT L_CREDITO
        FROM VENTA_POLITICA_PERSONA
        WHERE ID_POLITICA = 5
        AND ID_PERSONA = P_ID_PERSONA;

        RETURN(L_CREDITO);
    EXCEPTION
        WHEN OTHERS THEN
        L_CREDITO := 'N';

        RETURN(L_CREDITO);
    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
