CREATE OR REPLACE FUNCTION eliseo.pkg_sales_fc_get_tipo_asiento (P_ID_ENTIDAD bigint, P_ID_DEPTO text,P_ID_ANHO bigint,P_ID_TIPOVOUCHER bigint) RETURNS varchar AS $body$
DECLARE

        L_ID_TIPOASIENTO varchar(4);
        L_CONTAR bigint;

BEGIN
            SELECT COUNT(1) INTO STRICT L_CONTAR FROM CONTA_VOUCHER_CONFIG
            WHERE ID_ENTIDAD = P_ID_ENTIDAD
            AND ID_DEPTO = P_ID_DEPTO
            AND ID_ANHO = P_ID_ANHO
            AND ID_TIPOVOUCHER = P_ID_TIPOVOUCHER;

            IF L_CONTAR = 1 THEN
                SELECT ID_TIPOASIENTO INTO STRICT L_ID_TIPOASIENTO 
                FROM CONTA_VOUCHER_CONFIG
                WHERE ID_ENTIDAD = P_ID_ENTIDAD
                AND ID_DEPTO = P_ID_DEPTO
                AND ID_ANHO = P_ID_ANHO
                AND ID_TIPOVOUCHER = P_ID_TIPOVOUCHER;
            ELSE
                L_ID_TIPOASIENTO := 'X';
            END IF;
         RETURN L_ID_TIPOASIENTO;
    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
