CREATE OR REPLACE FUNCTION eliseo.pkg_sales_fc_precio_venta (P_ID_ALMACEN bigint,P_ID_ARTICULO bigint,P_ID_ANHO bigint) RETURNS bigint AS $body$
DECLARE

        L_PRECIO decimal(10,2) :=0;

BEGIN
    
        SELECT coalesce(PRECIO,0) INTO STRICT L_PRECIO FROM VENTA_PRECIO
        WHERE ID_ALMACEN = P_ID_ALMACEN
        AND ID_ARTICULO = P_ID_ARTICULO
        AND ID_ANHO = P_ID_ANHO;

        RETURN(L_PRECIO);
    EXCEPTION
        WHEN OTHERS THEN
        L_PRECIO := 0;

        RETURN(L_PRECIO);
    END;
