CREATE OR REPLACE PROCEDURE eliseo.pkg_sales_sp_eliminar_detalle (P_ID_VENTA bigint,P_ID_VDETALLE bigint,P_TIPO bigint) AS $body$
BEGIN

            if P_TIPO=0 then
                DELETE from VENTA_DETALLE where id_venta=P_ID_VENTA
                AND ID_VDETALLE=P_ID_VDETALLE;
            else
                DELETE from VENTA_DETALLE where id_venta=P_ID_VENTA;
            end if;
            CALL pkg_sales_sp_actualizar_total_venta(P_ID_VENTA);
    END;
$body$
