SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_sales,public;




CREATE OR REPLACE PROCEDURE eliseo.pkg_sales_sp_finalizar_transf_asiento (P_ID_TRANSFERENCIA bigint,P_ERROR OUT bigint,P_MSGERROR OUT text) AS $body$
DECLARE


        L_VENTA_ID_VOUCHER bigint := 0;
        L_ID_ASIENTO bigint := 0;
        L_ID_ORIGEN bigint;
        L_ID_OPERORIGEN bigint := 2;
        L_ID_FONDO bigint := 10;
        L_ID_DEPTO varchar(20):= '';
        L_ID_CUENTAAASI varchar(20):= '';
        L_CTACTE varchar(20):= '';
        L_RESTRICCION varchar(20):= '';
        L_IMPORTE bigint := 0;
        L_DESCRIPCION varchar(100):= '';
        L_ID_PARENT bigint := 0;
        L_AGRUPA varchar(1) := '';

        L_TOTALIZAR_IMPORTE bigint := 0;
        L_CANTIDAD_ASIENTOS bigint := 0;
        L_GLOSA varchar(200);
        L_DEBITOS decimal(10,2);
        L_TOTAL decimal(10,2);

        CURSOR_CASIENTOS CURSOR FOR 
        SELECT ID_FONDO, ID_DEPTO, ID_CUENTAAASI, ID_CTACTE, ID_RESTRICCION, IMPORTE, SUBSTR(DESCRIPCION,0,49), AGRUPA FROM VENTA_TRANSFERENCIA_ASIENTO WHERE ID_TRANSFERENCIA = P_ID_TRANSFERENCIA;

        L_ERROR bigint :=0;
        L_MSGERROR varchar(100) :='';

BEGIN
        --- Validar que haya contrapartida
        SELECT coalesce(SUM(IMPORTE),0),coalesce(SUM(CASE WHEN DC='D' THEN IMPORTE  ELSE 0 END ),0), COUNT(1) INTO STRICT L_TOTALIZAR_IMPORTE,L_DEBITOS, L_CANTIDAD_ASIENTOS  FROM VENTA_TRANSFERENCIA_ASIENTO WHERE ID_TRANSFERENCIA = P_ID_TRANSFERENCIA;
        IF L_CANTIDAD_ASIENTOS = 0 THEN
            L_ERROR := 1;
            L_MSGERROR := 'La Transferencia no tiene asientos contables.';
        ELSIF L_CANTIDAD_ASIENTOS > 0 AND L_TOTALIZAR_IMPORTE <> 0  THEN
            L_ERROR := 1;
            L_MSGERROR := 'La suma del debe y haber no igualan en cero (0).';
        END IF;

        IF L_ERROR <> 1 THEN
            SELECT ID_ENTIDAD||'-'||ID_DEPTO||'-'||SERIE||'-'||NUMERO||'-'||GLOSA, ID_VOUCHER, ABS(IMPORTE) INTO STRICT L_GLOSA, L_VENTA_ID_VOUCHER, L_TOTAL FROM VENTA_TRANSFERENCIA WHERE ID_TRANSFERENCIA = P_ID_TRANSFERENCIA;

            IF L_DEBITOS <> L_TOTAL THEN
                L_ERROR := 1;
                L_MSGERROR := 'ALTO: EL importe de la Transferencia es diferente al Asiento';
            END IF;
            IF L_ERROR <> 1 THEN
                OPEN CURSOR_CASIENTOS;
                FETCH CURSOR_CASIENTOS INTO L_ID_FONDO, L_ID_DEPTO, L_ID_CUENTAAASI, L_CTACTE, L_RESTRICCION, L_IMPORTE, L_DESCRIPCION, L_AGRUPA;
                WHILE CURSOR_CASIENTOS%FOUND LOOP
                    SELECT coalesce(MAX(ID_ASIENTO),0)+1 INTO STRICT L_ID_ASIENTO FROM CONTA_ASIENTO;

                    INSERT INTO CONTA_ASIENTO(ID_ASIENTO, ID_TIPOORIGEN, ID_ORIGEN, FONDO, DEPTO, CUENTA, CUENTA_CTE, RESTRICCION,IMPORTE, DESCRIPCION, MEMO, VOUCHER, REF_ID, AGRUPA) 
                    VALUES (L_ID_ASIENTO, L_ID_OPERORIGEN, P_ID_TRANSFERENCIA, L_ID_FONDO, L_ID_DEPTO, L_ID_CUENTAAASI, L_CTACTE, L_RESTRICCION,L_IMPORTE, L_GLOSA||'-'||L_DESCRIPCION, (L_ID_OPERORIGEN || '-' || P_ID_TRANSFERENCIA), L_VENTA_ID_VOUCHER, NULL,L_AGRUPA
                    );

                    FETCH CURSOR_CASIENTOS INTO L_ID_FONDO, L_ID_DEPTO, L_ID_CUENTAAASI, L_CTACTE, L_RESTRICCION, L_IMPORTE, L_DESCRIPCION, L_AGRUPA;

                END LOOP;
                CLOSE CURSOR_CASIENTOS;
            END IF;
        END IF;

        P_ERROR := L_ERROR;
        P_MSGERROR := L_MSGERROR;
    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
