SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_sales_facturacion,public;




CREATE OR REPLACE PROCEDURE eliseo.pkg_sales_facturacion_sp_venta_electronica_nube (P_ID_VENTA bigint) AS $body$
DECLARE

        L_ID_ENTIDAD bigint;
        L_ID_DEPTO varchar(11);
        L_RUC varchar(11);
        L_ID_COMPROBANTE varchar(3);
        L_COD_ELECTRONICO varchar(3);
        L_SERIE varchar(4);
        L_NUMERO varchar(8);
        L_DET varchar(4000);
        L_EMISOR_ID bigint:=1; --EMPRESA CON RUC UPeU
        L_HASH varchar(50);
        L_ORIGENID bigint := 0;
        L_TIPOEMISORID varchar(20);


BEGIN  
    
        
        select coalesce(max(origenid),0) into STRICT L_ORIGENID from eliseo.venta_electronica_nube
        where origenid = P_ID_VENTA;

        IF L_ORIGENID = 0 THEN 
        --IF 7 = 7 THEN 
        
            --SELECT ID_ENTIDAD,ID_DEPTO, ID_COMPROBANTE,SERIE,NUMERO INTO L_ID_ENTIDAD,L_ID_DEPTO,L_ID_COMPROBANTE,L_SERIE,L_NUMERO
            --FROM VENTA
            --WHERE ID_VENTA = P_ID_VENTA;
    
           	SELECT V.ID_ENTIDAD,V.ID_DEPTO, V.ID_COMPROBANTE,TC.COD_ELECTRONICO,V.SERIE,V.NUMERO 
			INTO STRICT L_ID_ENTIDAD,L_ID_DEPTO,L_ID_COMPROBANTE,L_COD_ELECTRONICO,L_SERIE,L_NUMERO
			FROM VENTA V
			INNER JOIN ELISEO.TIPO_COMPROBANTE TC ON V.ID_COMPROBANTE = TC.ID_COMPROBANTE
			WHERE ID_VENTA = P_ID_VENTA;--Agregado por Ulices
			
            SELECT B.ID_RUC, EMISORID, TIPOEMISORID INTO STRICT L_RUC, L_EMISOR_ID, L_TIPOEMISORID
            FROM CONTA_ENTIDAD A, CONTA_EMPRESA B
            WHERE A.ID_EMPRESA = B.ID_EMPRESA
            AND A.ID_ENTIDAD = L_ID_ENTIDAD;

            IF L_COD_ELECTRONICO = '03' OR L_COD_ELECTRONICO = '01' THEN
                SELECT (pkg_sales_facturacion_fc_venta(P_ID_VENTA)) INTO STRICT L_DET;
            END IF;
            IF L_COD_ELECTRONICO = '07' THEN
                SELECT (pkg_sales_facturacion_fc_nota_credito(P_ID_VENTA)) INTO STRICT L_DET;
            END IF;
            IF L_COD_ELECTRONICO = '08' THEN
                SELECT (pkg_sales_facturacion_fc_nota_debito(P_ID_VENTA)) INTO STRICT L_DET;
            END IF;
            --FACTURACION ELECTRONICA LAMB
            IF L_TIPOEMISORID = 'NUBE' THEN

                IF L_ID_ENTIDAD != 7922 THEN   -- Entidad prueba de iatec
                    NULL;
                    -- DESHABILITADO POR COBRO DE CUOTA EXTEMPORANEO ABRIL 2022
                     INSERT INTO VENTA_ELECTRONICA_NUBE(EMISORID,ORIGENID,FECHA,TIPO,NROCOMPROBANTE,COMPROBANTE,NOMBREARCHIVO, PROCESADO, ENVIADO)
                     VALUES (L_EMISOR_ID,P_ID_VENTA,clock_timestamp(),L_ID_COMPROBANTE,L_SERIE||'-'||L_NUMERO,L_DET,L_RUC||'-'||L_ID_COMPROBANTE||'-'||L_SERIE||'-'||L_NUMERO||'.txt', 0, 0);
                END IF;

            ELSE --UPEU -OTRO SERVER EFAC
                    --FACTURACION ELECTRONICA LAMB
                    INSERT INTO VENTA_ELECTRONICA(EMISORID,ORIGENID,FECHA,TIPO,NROCOMPROBANTE,COMPROBANTE,NOMBREARCHIVO)
                    VALUES (L_EMISOR_ID,P_ID_VENTA,clock_timestamp(),L_ID_COMPROBANTE,L_SERIE||'-'||L_NUMERO,L_DET,L_RUC||'-'||L_ID_COMPROBANTE||'-'||L_SERIE||'-'||L_NUMERO||'.txt');
                    --FACTURACION ELECTRONICA CONTAWEB
                    INSERT INTO UPEU_COMPROBANTE@DBL_ARON_APP(EMISORID,ORIGENID,FECHA,TIPO,NROCOMPROBANTE,COMPROBANTE,NOMBREARCHIVO)
                    VALUES (L_EMISOR_ID,P_ID_VENTA,clock_timestamp(),L_ID_COMPROBANTE,L_SERIE||'-'||L_NUMERO,L_DET,L_RUC||'-'||L_ID_COMPROBANTE||'-'||L_SERIE||'-'||L_NUMERO||'.txt');

            END IF;
            --HACEMOS UNA PAUSA DE 2 SEGUNDOS PARA DEVOLVER EL VALOR RESUMEN
            --PKG_SALES.SP_SLEEP(2);
            --APEX_UTIL.PAUSE(4);
        END IF;


    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
